        top.location.href = window.location.href;
    }

    var iframeClosed = readCookie('g1_tf_iframe_closed');

    if (iframeClosed) {
        document.getElementsByTagName('html')[0].className += ' g1-no-iframe-bar';
    }

    function closeIframe () {
        document.getElementsByTagName('html')[0].className += ' g1-no-iframe-bar';
        createCookie('g1_tf_iframe_closed', 1);
    }

    function createCookie (name,value,time) {
        var expires;

        if (time) {
            var date = new Date();
            var ms = time;

            if (typeof time === 'object') {
                ms = time.value;

                switch (time.type) {
                    case 'days':
                        ms = ms * 24 * 60 * 60 * 1000;
                        break;
                }
            }

            date.setTime(date.getTime() + ms);
            expires = '; expires=' + date.toGMTString();
        }
        else {
            expires = '';
        }

        document.cookie = name + '=' + value + expires + '; path=/';
    }

    function readCookie (name) {
        var nameEQ = name + '=';
        var ca = document.cookie.split(';');

        for(var i = 0; i < ca.length; i += 1) {
            var c = ca[i];
            while (c.charAt(0) === ' ') {
                c = c.substring(1,c.length);
            }

            if (c.indexOf(nameEQ) === 0) {
                return c.substring(nameEQ.length,c.length);
            }
        }

        return null;
    }

    function removeCookie (name) {
        createCookie(name, '', -1);
    }

    // live namespace
    window.g1Iframe = {};

    g1Iframe.isClosed = iframeClosed;
    g1Iframe.close = closeIframe;
})();</script>
